import React, { useState, useEffect } from 'react';
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import "react-big-calendar/lib/css/react-big-calendar.css";
import { Link } from 'react-router-dom';

const PatientAppointment = () => {
  const [patientUserId, setPatientUserId] = useState(''); // To store the patient's user ID
  const [isError, setIsError] = useState(false);
  const [appointments, setAppointments] = useState([
    {
      title: '',
      start: new Date(),
      end: new Date(),  // 5:00 PM
      appointmentId: '',
      appointmentStatus: ''
    },
  ]);
  useEffect(() => {
    // Replace 'http://localhost:8080' with your actual API URL
    fetch('http://localhost:8080/checkLoggedInPatient')
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok');
      })
      .then((data) => {
        setPatientUserId(data);
        // Once you have the patientUserId, make another request to get appointments
        fetch(`http://localhost:8080/appointments?patientUserId=${data}`)
          .then((appointmentsResponse) => {
            if (appointmentsResponse.ok) {
              return appointmentsResponse.json();
            }
            throw new Error('Network response was not ok');
          })
          .then((appointmentsData) => {
            const formattedAppointments = appointmentsData.map((appointment) => {
              // Extract date and time components
              const [year, month, day] = appointment.scheduleDate.split('-').map(Number);
              const [hours, minutes] = appointment.startTime.split(':').map(Number);
              const [hours2, minutes2] = appointment.endTime.split(':').map(Number);

              // Create Date objects for start and end times
              const startDate = new Date(year, month - 1, day, hours, minutes);
              const endDate = new Date(year, month - 1, day, hours2, minutes2);

              // Create an appointment object
              return {
                title: 'Dr. ' + appointment.doctorName,
                start: startDate,
                end: endDate,
                appointmentId: appointment.transactionNo,
                appointmentStatus: appointment.status
              };
            });

            setAppointments(formattedAppointments);
          })
          .catch((error) => {
            // Handle errors
            console.error(error);
          });

      })
      .catch((error) => {
        setIsError(true);
        console.error('Error:', error);
      });
  }, []);

  const locales = {
    "en-US": require("date-fns/locale/en-US")
  }

  const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales
  })

  const handleCancel = async (appointmentId) => {
    try {
      const response = await fetch(`http://localhost:8080/appointmentChange/${appointmentId}?newStatus=Cancelled`, {
        method: 'PUT',
      });

      if (response.ok) {
        // Handle success if needed
        console.log('Appointment cancelled successfully');
        window.location.reload();
      } else {
        setIsError(true);
      }
    } catch (error) {
      console.error('Error cancelling appointment:', error);
      setIsError(true);
    }
  };


  return (
    <div>
      <PatientNavBar />
      <div style={{ display: "flex", margin: "auto", width: "100vw", justifyContent: "center" }}>
      <Calendar
        localizer={localizer}
        events={appointments.filter(appointment => appointment.appointmentStatus === "Approved by Doctor")}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 600, width: "65%" }}
      />


        <div style={{ marginLeft: "30px" }}>
          <h1>My Appointments</h1>
          {appointments.map((appointment, index) => (
            // Check if appointment status is "Approved by Doctor"
            appointment.appointmentStatus === "Approved by Doctor" && (
              <table key={index}>
                <tr>
                  <td rowSpan={2} width={200} style={{ border: "1px solid lightgray", backgroundColor: "lightgray", paddingLeft: "10px" }}>
                    {appointment.title} <br />
                    {format(appointment.start, "MM/dd/yyyy EEEE")} <br />
                    {format(appointment.start, "h:mm a")} - {format(appointment.end, "h:mm a")}
                  </td>
                  <td><Link to="/patresched"><button style={{ borderRadius: 0, width: "100%" }}>Reschedule</button></Link></td>
                </tr>
                <tr>
                  <td><button className='cancel' onClick={() => handleCancel(appointment.appointmentId)} type="submit">Cancel</button></td>
                </tr>
              </table>
            )
          ))}
        </div>

      </div>
      <PatientFooter />
    </div>
  );
}

export default PatientAppointment;
