import React, { useState, useEffect } from 'react';
import "../styles/Login.css";

function PatientLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState('');

  useEffect(() => {
    // Check if the user is already logged in (you can implement your own logic)
    const isLoggedIn = false; // Replace with your actual login check logic

    if (isLoggedIn) {
      // Redirect to the profile page if already logged in
      window.location.href = '/profile';
    }
  }, []);

  const handleLogin = async () => {
    try {
      const url = new URL('http://localhost:8080/patientlogin');
      url.searchParams.append('username', username);
      url.searchParams.append('password', password);

      const response = await fetch(url, {
        method: 'POST',
      });

      if (response.ok) {
        // Login successful
        setLoginError('');
        console.log('Login successful');
        window.location.href = '/patienthome'; // Redirect to the profile page
      } else {
        // Login failed
        const errorMessage = await response.text();
        setLoginError(errorMessage);
        console.log('Login failed:', errorMessage);
      }
    } catch (error) {
      console.error('Error during login:', error);
      setLoginError('An error occurred during login');
    }
  };

  const handleToSignup = () => {
    window.location.href = '/patientreg';
  };

  return (
    <div>
    <div className="container" id="container">
              <div id="navbar">
            <nav>
                <ul>
                    <li style={{float:"left"}}><a>Doc Click Connect</a></li>
                    <li style={{float:"right"}}><a href='/patientreg'>Register</a></li>
                    <li style={{float:"right"}}><a href='/patientlogin'>Login</a></li>
                </ul>
            </nav>
        </div>
      <div className="form-container sign-in-container">
        <form action="#" id="signin-form">
          <h1 style={{ color: '#0094d4' }}>Patient Login</h1>
          <div className="infield">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="infield">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          {/* <a href="#" class="forgot">Forgot your password?</a> */}
          <button type="submit" onClick={handleLogin}>
            Login
          </button>
        </form>
      </div>
      <div className="overlay-container" id="overlayCon">
        <div className="overlay">
          <div className="overlay-panel overlay-right">
            <h1 style={{ color: '#ffffff' }}>Hello there!</h1>
            <p>
              Set an appointment now! <br /> Enter your details now!
            </p>
            <button onClick={handleToSignup}>Register</button>
          </div>
        </div>
      </div>

    </div>
        </div>


  );
}

export default PatientLogin;
