import "../../styles/Search.css";
import DoctorNavbar from '../../components/DoctorNavbar';
import DoctorFooter from '../../components/DoctorFooter';
import React, { useState, useEffect } from 'react';

const DoctorClinics= () => {
  const [schedules, setSchedules] = useState([]);
  const [loading, setLoading] = useState(true);
  const [clinicsResponse, setClinicsResponse] = useState('');
  const [doctorUserId, setDoctorUserId] = useState(''); // To store the patient's user ID
  const [isError, setIsError] = useState(false);
  const [clinic, setClinic] = useState([
    {
      name: '',
      address: '',
      doctor: ''
    },
  ]);

  useEffect(() => {
      // Replace 'http://localhost:8080' with your actual API URL
      fetch('http://localhost:8080/checkLoggedInDoctor')
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error('Network response was not ok');
        })
        .then((data) => {
          setDoctorUserId(data);
          // Once you have the patientUserId, make another request to get appointments
          fetch(`http://localhost:8080/docsched/${data}`)
            .then((clinicsResponse) => {
              if (clinicsResponse.ok) {
                return clinicsResponse.json();
              }
              throw new Error('Network response was not ok');
            })
            .then((clinicData) => {
              const formattedClinics = clinicData.map((clinic) => {
                return {
                  name: '' + clinic.clinics.name,
                  address: '' + clinic.clinics.address,
                  doctor: ''+ clinic.doctor.user.firstName 
                };
              });
  
              setClinic(formattedClinics);
            })
            .catch((error) => {
              // Handle errors
              console.error(error);
            });
  
        })
        .catch((error) => {
          setIsError(true);
          console.error('Error:', error);
        });
    }, []);


  // if (loading) {
  //   return <p>Loading...</p>;
  // }

  const handleEdit = () => {
    console.log('Edit clicked for clinic:', );
  };

  const handleDelete = () => {

    console.log('Delete clicked for clinic:', );
  };

  return (
    <div className="info-container" id="container">
      <DoctorNavbar />
      <div className="info-container">
      {clinic.map((clinic, index) => (
        <div className="doctor-grid">
        
        <div className="doctor-card ">
          <div className="doctor-info">
            <h2>{clinic.name}</h2>
            <p>{clinic.address}</p>
            <p>{clinic.doctor}</p>
            <button className="clinic-button" >Edit</button>
            <button className="clinic-button" >Delete</button>
          </div>
          
          </div>
              
        </div>
        ))}
      </div>
      <DoctorFooter />
    </div>
  );
};

export default DoctorClinics;
