import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import DoctorCalendar from '../../components/DoctorCalendar';
import { useParams } from 'react-router-dom';


const SetAppointment = () => {
  const {doctorId} = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [clinics, setClinics] = useState([
    {
      clinicId: '',
      clinicName: '',
      address: '',
      number: '',
      email: ''
    },
  ]);
  const [schedule, setSchedules] = useState([
    {
      clinicId: '',
      scheduleDay: '',
      start: new Date(),
      end: new Date(),  // 5:00 PM
      slots: ''
    },
  ]);

  useEffect(() => {
    const fetchDoctorSchedules = async () => {
      try {
        const response = await fetch(`http://localhost:8080/docsched/${doctorId}`);

        if (response.ok) {
          const data = await response.json();
                const formattedSched = {
                  clinicId: data.clinicId,
                  scheduleDay: data.scheduleDay,
                  start: startDate,
                  end: endDate,
                  slots: data.slots
                };

                const formattedClinic = {
                  clinicId: data.clinic.clinicId,
                  clinicName: data.clinic.name,
                  address: data.clinic.address,
                  number: data.clinic.officeNumber,
                  email: data.clinic.officeEmail
                }

                setSchedules([formattedSched]);
                setClinics([formattedClinic]);

        } else {
          setError('Error fetching appointment');
        }
      } catch (error) {
        setError('Error fetching appointment');
      }
    };

    fetchDoctorSchedules();
  }, [doctorId]);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
    <PatientNavBar />
    <div style={{ display: "flex", margin: "auto", width: "100vw", justifyContent: "center" }}>
        <DoctorCalendar />

        <div style={{ marginLeft: "30px" }}>
          <h1>Dr. John Cruz</h1>
          <h2>Specialization: Cardiologist</h2>
          <h3>Clinic Schedule: Monday (2:00PM-5:00PM)</h3>
          <h3>Contact Number: +63 999 999 9999</h3>
          <h3>Email Address: johncruz@gmail.com</h3>
          <br/>

          <h1>Existing Appointments </h1>
          <table>
            <tr>
              <td rowSpan={2} width={200} style={{ border: "1px solid lightgray", backgroundColor: "lightgray", paddingLeft: "10px" }}>11/06/23 MONDAY <br/> 2:00-5:00 PM</td>
              <td><Link to="/patresched"><button style={{ borderRadius: 0, width: "250px" }}>Reschedule</button></Link></td>
            </tr>
            <tr><td><button className='cancel'>Cancel</button></td></tr>
          </table>
        </div>
      </div>
    <PatientFooter />
    </div>
  );
};

export default SetAppointment;
