import React, { useState, useEffect } from 'react';
import "../../styles/Profile.css";
import DoctorNavbar from '../../components/DoctorNavbar';
import DoctorFooter from '../../components/DoctorFooter';
import Popup from '../../components/Popup';
import AvatarSelectionPopup from '../../components/AvatarSelection';
import avatar00 from '../../images/profilepic.jpg';
import avatar01 from '../../assets/DoctorIcons/Icon01.png';
import avatar02 from '../../assets/DoctorIcons/Icon02.png';
import avatar03 from '../../assets/DoctorIcons/Icon03.png';
import avatar04 from '../../assets/DoctorIcons/Icon04.png';
import avatar05 from '../../assets/DoctorIcons/Icon05.png';
import avatar06 from '../../assets/DoctorIcons/Icon06.png';
import avatar07 from '../../assets/DoctorIcons/Icon07.png';
import avatar08 from '../../assets/DoctorIcons/Icon08.png';
import avatar09 from '../../assets/DoctorIcons/Icon09.png';
import avatar10 from '../../assets/DoctorIcons/Icon10.png';
import avatar11 from '../../assets/DoctorIcons/Icon11.png';
import avatar12 from '../../assets/DoctorIcons/Icon12.png';
import avatar13 from '../../assets/DoctorIcons/Icon13.png';
import avatar14 from '../../assets/DoctorIcons/Icon14.png';

  
const EditDoctorProfile = () => {
    const [avatar, setAvatar] = useState('');
    const [selectedAvatar, setSelectedAvatar] = useState('');
    const [isAvatarSelectionOpen, setAvatarSelectionOpen] = useState(false);
    const [user, setUser] = useState(null);
    const [isError, setIsError] = useState(false);
    const [username, setUsername] = useState('');
    const [firstName, setFirstName] = useState('');
    const [middleName, setMiddleName] = useState('');
    const [lastName, setLastName] = useState('');
    const [age, setAge] = useState('');
    const [sex, setSex] = useState('');
    const [birthday, setBirthday] = useState('');
    const [address, setAddress] = useState('');
    const [contactNumber, setContactNumber] = useState('');
    const [email, setEmail] = useState('');
    const [credentials, setCredentials] = useState('');
    const [specialization, setSpecialization] = useState('');
    const [prcId, setPrcId] = useState('');
    const [password, setPassword] = useState('');
    const [retypePassword, setRetypePassword] = useState('');
    const [editMessage, setEditMessage] = useState('');
    const [userType] = useState('doctor');
    const [secretary, setSecretary] = useState('');
  
        
    const setDisplayedAvatars = (avatar) => {
        switch (avatar) {
        case 'avatar01':
            setSelectedAvatar(avatar01);
        case 'avatar02':
            setSelectedAvatar(avatar02);
        case 'avatar03':
            setSelectedAvatar(avatar03);
        case 'avatar04':
            setSelectedAvatar(avatar04);
        case 'avatar05':
            setSelectedAvatar(avatar05);
        case 'avatar06':
            setSelectedAvatar(avatar06);
        case 'avatar07':
            setSelectedAvatar(avatar07);
        case 'avatar08':
            setSelectedAvatar(avatar08);
        case 'avatar09':
            setSelectedAvatar(avatar09);
        case 'avatar10':
            setSelectedAvatar(avatar10);
        case 'avatar11':
            setSelectedAvatar(avatar11);
        case 'avatar12':
            setSelectedAvatar(avatar12);
        case 'avatar13':
            setSelectedAvatar(avatar13);
        case 'avatar14':
            setSelectedAvatar(avatar14);
        default:
            setSelectedAvatar(avatar00);
        }
    };


    const handleAvatarSelection = (selectedAvatar) => {
        setSelectedAvatar(selectedAvatar);
        switch (selectedAvatar) {
            case avatar01:
                setAvatar('avatar01');
            case avatar02:
                setAvatar('avatar02');
            case avatar03:
                setAvatar('avatar03');
            case avatar04:
                setAvatar('avatar04');
            case avatar05:
                setAvatar('avatar05');
            case avatar06:
                setAvatar('avatar06');
            case avatar07:
                setAvatar('avatar07');
            case avatar08:
                setAvatar('avatar08');
            case avatar09:
                setAvatar('avatar09');
            case avatar10:
                setAvatar('avatar10');
            case avatar11:
                setAvatar('avatar11');
            case avatar12:
                setAvatar('avatar12');
            case avatar13:
                setAvatar('avatar13');
            case avatar14:
                setAvatar('avatar14');
            default:
                setAvatar('avatar00');
            }
        setAvatarSelectionOpen(false);
        console.log(selectedAvatar);
        console.log(avatar);
      };

    useEffect(() => {
        fetchUser();
      }, []);

    const fetchUser = async () => {
        try {
          const response = await fetch("http://localhost:8080/doctorprofile");
          if (response.ok) {
            const data = await response.json();
            setUser(data);
            setUsername(data.user.username);
            setFirstName(data.user.firstName);
            setMiddleName(data.user.middleName);
            setLastName(data.user.lastName);
            setAge(data.user.age);
            setSex(data.user.sex);
            setBirthday(data.user.birthday);
            setAddress(data.user.address);
            setContactNumber(data.user.contactNumber);
            setEmail(data.user.email);
            setCredentials(data.credentials);
            setPrcId(data.prcId);
            setSpecialization(data.specialization);
            setSecretary(data.secretary);
            setAvatar(data.user.avatar);
          } else {
            setIsError(true);
          }
        } catch (error) {
          console.error('Error fetching user:', error);
          setIsError(true);
        }
    };

    const handleEdit = async () => {
        // Check if any of the fields are empty
        if (
            !username ||
            !firstName ||
            !middleName ||
            !lastName ||
            !age ||
            !sex ||
            !birthday ||
            !address ||
            !contactNumber ||
            !email
            ||
            !prcId
        ) {
          setEditMessage('Please fill in all required fields.');
          return;
        }
      
        // Validate email format using regular expression
        const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
        if (!emailRegex.test(email)) {
          setEditMessage('Invalid email format.');
          return;
        }
      
        try {
          const url = new URL('http://localhost:8080/editdoctor');
          const userData = {
            user: {
            username,
            firstName,
            middleName,
            lastName,
            age,
            sex,
            birthday,
            address,
            contactNumber,
            email,
            avatar
          },
            doctor: {
            prcId,
            specialization,
            credentials,
            secretary
            }
          };
      
          const response = await fetch(url, {
            method: 'PUT', // Use PUT method for editing
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
          });
      
          if (response.ok) {
            // Edit successful
            setEditMessage('User details updated successfully');
          } else {
            // Edit failed
            const errorMessage = await response.text();
            setEditMessage(errorMessage);
            // Handle the error or display an error message to the user
          }
        } catch (error) {
          console.error('Error during edit:', error);
          setEditMessage('Error during edit. Please try again later.');
          // Handle the error or display an error message to the user
        }
      };
      useEffect(() => {
        setDisplayedAvatars(avatar);
    }, [avatar]);  // Add the dependency array to avoid unnecessary re-renders
    
  return (
    <div className="profile-container" id="container">
        <DoctorNavbar />
        <div className="editing-container"> 

        {/*   <Popup trigger={true}>
                <h2>Please enter your current password:</h2>
                <div className="profile-infield">
                    <input type="text" placeholder="Enter password" />
                </div>
            </Popup>
        */} 
        
            <div className="edit-steps">
                    <div className="edit-card" style={{marginTop:'1550px'}}>
                        <h1>Edit Doctor Profile</h1>

                        <div className='container-card'>
                            <b>Profile Picture:</b>
                            <div className="avatar-selection">
                            <div className="selected-avatar" onClick={() => setAvatarSelectionOpen(true)}>
                                {selectedAvatar && <img src={selectedAvatar} alt="Selected Avatar" />}
                                <br />
                                <button type="submit">Change Avatar</button>
                                
                            </div>
                            </div>
                        </div>

                        <br />
                        <div className='container-card'>
                            <b>Username:</b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="username"
                                        placeholder="Change Username"
                                        name="username"
                                        value={username}
                                        onChange={(e) => setUsername(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>First Name:</b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="firstName"
                                        placeholder="Change First Name"
                                        name="firstName"
                                        value={firstName}
                                        onChange={(e) => setFirstName(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Middle Name:</b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="middleName"
                                        placeholder="Change Middle Name"
                                        name="middleName"
                                        value={middleName}
                                        onChange={(e) => setMiddleName(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Last Name:</b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="lastName"
                                        placeholder="Change Last Name"
                                        name="lastName"
                                        value={lastName}
                                        onChange={(e) => setLastName(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Contact Number:</b>
                            <div className="profile-infield">                                
                                <input type="text"
                                        id="contactNumber"
                                        placeholder="Change Contact Number"
                                        name="contactNumber"
                                        value={contactNumber}
                                        onChange={(e) => setContactNumber(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Email:</b>
                            <div className="profile-infield">
                            <input type="text"
                                        id="email"
                                        placeholder="Change Email"
                                        name="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Sex:</b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="sex"
                                        placeholder="Change SSex"
                                        name="sex"
                                        value={sex}
                                        onChange={(e) => setSex(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Age:</b>
                            <div className="profile-infield">
                                <input type="number" placeholder="Change Age" />
                                <input type="text"
                                        id="age"
                                        placeholder="Change Age"
                                        name="age"
                                        value={age}
                                        onChange={(e) => setAge(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Birthday:</b>
                            <div className="profile-infield">
                                <input type="date"
                                        id="birthday"
                                        placeholder="Change Birthday"
                                        name="birthday"
                                        value={birthday}
                                        onChange={(e) => setBirthday(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Address: </b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="address"
                                        placeholder="*(House/Lot/Unit No., Street, Barangay, City/Town, Province)"
                                        name="address"
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                            <div className='container-card'>
                            <b>PRC ID No: </b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="prcId"
                                        placeholder="Change PRC ID No."
                                        name="prcId"
                                        value={prcId}
                                        onChange={(e) => setPrcId(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Specialization: </b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="specialization"
                                        placeholder="Change Specialization"
                                        name="specialization"
                                        value={specialization}
                                        onChange={(e) => setSpecialization(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Credentials: </b>
                            <div className="profile-infield">
                                <input type="text"
                                        id="credentials"
                                        placeholder="Change Credentials"
                                        name="credentials"
                                        value={credentials}
                                        onChange={(e) => setCredentials(e.target.value)}/>
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Password:</b>
                            <div className="profile-infield">
                                <input type="password" placeholder="Change Password" />
                            </div>
                        </div>
                        <br />
                        <div className='container-card'>
                            <b>Confirm Password: </b>
                            <div className="profile-infield">
                                <input type="password" placeholder="Confirm Password" />
                            </div>
                        </div>
                        <br />
                        <button type="button"  onClick={handleEdit}>Save Changes</button>
                        {editMessage && <p>{editMessage}</p>}
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />

                    </div>
            </div>
        </div>
        <DoctorFooter />
        {isAvatarSelectionOpen && (
         <AvatarSelectionPopup
         avatars={[avatar01, avatar02, avatar03, avatar04, avatar05,
           avatar06, avatar07, avatar08, avatar09, avatar10,
           avatar11, avatar12, avatar13, avatar14]}
         selectedAvatar={avatar}
         onSelectAvatar={(avatar) => handleAvatarSelection(avatar)}
         onClose={() => setAvatarSelectionOpen(false)}
       />
      )}
    </div>
  );
};

export default EditDoctorProfile;
