import React, { useState, useEffect } from 'react';
import "../../styles/Search.css";
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import DoctorCard from '../../components/Doctors'; // Import the DoctorCard component


const DoctorSearch = () => {
  // Sample doctor data (you can replace it with your own data)
  const [doctors, setDoctors] = useState([
    {
      doctorId: '',
      firstName: '',
      lastName: '',
      contactNumber: '',
      specialization: '',
      credentials: '',
      avatar:'',
    }
  ]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);


  useEffect(() => {
    const fetchAllDoctors = async () => {
      try {
        const response = await fetch('http://localhost:8080/allusers');

        if (response.ok) {
          const data = await response.json();
          const formattedDoctors = data.map((doctorData) => ({
            doctorId: doctorData.userId,
            firstName: `Dr. ${doctorData.user.firstName}`,
            lastName: doctorData.user.lastName,
            contactNumber: doctorData.user.contactNumber,
            specialization: doctorData.specialization,
            credentials: doctorData.credentials,
            avatar: doctorData.user.avatar
          }));

          setDoctors(formattedDoctors);
        } else {
          setError('Failed to fetch doctors');
        }
      } catch (error) {
        setError('Error while fetching doctors');
      } finally {
        setIsLoading(false);
      }
    };

    fetchAllDoctors();
  }, []);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  //get all schedules

  //set variables
  //clinicId
  //clinic.name
  //clinic.address
  //clinic.number
  //scheduleDay
  //slots
  //doctorUserId

  //same scheduledays are counted as one
  //same clinicId counted as one
  //filter through doctorUserId
  //display clinicname, address number & doctoravatar

  return (
    <div className="search-container" id="container">
      <PatientNavBar />
      <div className="info-container">
        <div class="search-box" style={{position: "sticky"}}>
          <input type="text" class="search-input" placeholder="Find the doctor you need.."/>
          <button class="search-button">Search</button>
        </div>
        <br />
        <div style={{ display: "block", alignItems: "center",  marginRight: "auto", width: "90%" }}>
          <div className="doctor-grid">
            {doctors.map((doctor, index) => (
              <DoctorCard key={index} doctor={doctor} />
            ))}
          </div>
        </div>
      </div>
      <PatientFooter />
    </div>
  );
};

export default DoctorSearch;
