import React, { useState, useEffect } from 'react';
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import addWeeks from 'date-fns/addWeeks';
import format from 'date-fns/format';
import "react-big-calendar/lib/css/react-big-calendar.css";
import { useParams } from 'react-router-dom';

const PatientResched = () => {
  const {appointmentId} = useParams();
  const [patientUserId, setPatientUserId] = useState(''); // To store the patient's user ID
  const [isError, setIsError] = useState(false);
  const [error, setError] = useState('');
  const [latestAppointment, setLatestAppointment] = useState([
    {
      title: '',
      start: new Date(),
      end: new Date(),  // 5:00 PM
      transactionNo: '',
      appointmentStatus: ''
    },
  ]);
  const [appointments, setAppointments] = useState([
    {
      title: '',
      start: new Date(),
      end: new Date(),  // 5:00 PM
      transactionNo: '',
      appointmentStatus: ''
    },
  ]);



  useEffect(() => {
    // Replace 'http://localhost:8080' with your actual API URL
    fetch('http://localhost:8080/checkLoggedInPatient')
      .then((response) => {
        if (response.ok) {
          console.log(appointmentId);
          return response.json();
        }
        throw new Error('Network response was not ok');
      })
      .then((data) => {
        setPatientUserId(data);
        // Once you have the patientUserId, make another request to get appointments
        fetch(`http://localhost:8080/appointments?patientUserId=${data}`)
          .then((appointmentsResponse) => {
            if (appointmentsResponse.ok) {
              return appointmentsResponse.json();
            }
            throw new Error('Network response was not ok');
          })
          .then((appointmentsData) => {
            const formattedAppointments = appointmentsData.map((appointment) => {
              // Extract date and time components
              const [year, month, day] = appointment.scheduleDate.split('-').map(Number);
              const [hours, minutes] = appointment.startTime.split(':').map(Number);
              const [hours2, minutes2] = appointment.endTime.split(':').map(Number);

              // Create Date objects for start and end times
              const startDate = new Date(year, month - 1, day, hours, minutes);
              const endDate = new Date(year, month - 1, day, hours2, minutes2);

              // Create an appointment object
              return {
                title: 'Dr. ' + appointment.doctorName,
                start: startDate,
                end: endDate,
                transactionNo: appointment.transactionNo,
                appointmentStatus: appointment.status
              };
            });

            setAppointments(formattedAppointments);
          })
          .catch((error) => {
            // Handle errors
            console.error(error);
          });

      })
      .catch((error) => {
        setIsError(true);
        console.error('Error:', error);
      });
  }, []);

  useEffect(() => {
    const fetchAppointment = async () => {
      try {
        const response = await fetch(`http://localhost:8080/appointment/${appointmentId}`);

        if (response.ok) {
          const data = await response.json();

          // Check if necessary properties are present in the data object
          if (data && data.scheduleDate && data.startTime && data.endTime && data.doctorName && data.transactionNo && data.status) {
            const [year, month, day] = data.scheduleDate.split('-').map(Number);
            const [hours, minutes] = data.startTime.split(':').map(Number);
            const [hours2, minutes2] = data.endTime.split(':').map(Number);

            // Check if the extracted components are valid numbers
            if (!isNaN(year) && !isNaN(month) && !isNaN(day) && !isNaN(hours) && !isNaN(minutes) && !isNaN(hours2) && !isNaN(minutes2)) {
              // Create Date objects for start and end times
              const startDate = new Date(year, month - 1, day, hours, minutes);
              const endDate = new Date(year, month - 1, day, hours2, minutes2);

              // Check if the created Date objects are valid
              if (!isNaN(startDate.getTime()) && !isNaN(endDate.getTime())) {
                const formattedAppointment = {
                  title: `Dr. ${data.doctorName}`,
                  start: startDate,
                  end: endDate,
                  transactionNo: data.transactionNo,
                  appointmentStatus: data.status
                };

                setLatestAppointment([formattedAppointment]);
              } else {
                console.error('Invalid date created. Check the date and time components.');
              }
            } else {
              console.error('Invalid date/time components in the response data.');
            }
          } else {
            console.error('Incomplete or missing data in the response.');
          }
        } else {
          setError('Error fetching appointment');
        }
      } catch (error) {
        setError('Error fetching appointment');
      }
      console.log(scheduleDate);
    };

    fetchAppointment();
  }, [appointmentId]);

  const handleReschedule = async () => {
    try {
      const response = await fetch(`http://localhost:8080/appointment/${appointmentId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          // Add additional headers if needed, e.g., 'Authorization': 'Bearer YOUR_ACCESS_TOKEN'
        },
        body: JSON.stringify({ scheduleDate: scheduleDate }),
      });

      if (!response.ok) {
        throw new Error(`Failed to update appointment: ${response.statusText}`);
      }

      const data = await response.json();
      console.log(data); // Handle the response data as needed

    } catch (error) {
      console.error('Error updating appointment:', error.message);
      // Handle errors here
    }
  };


    const locales = {
        "en-US": require("date-fns/locale/en-US")
      }

      const localizer = dateFnsLocalizer({
        format,
        parse,
        startOfWeek,
        getDay,
        locales
      })

      // Define your appointment data

      return (
        <div>
          <PatientNavBar />
          <div style={{ display: "flex", margin: "auto", width: "100vw", justifyContent: "center" }}>
          <Calendar
            localizer={localizer}
            events={appointments.filter(appointment => appointment.appointmentStatus === "Approved by Doctor")}
            startAccessor="start"
            endAccessor="end"
            style={{ height: 600, width: "65%" }}
          />

    <div style={{ marginLeft: "30px" }}>
    <h1>My Appointments</h1>
    {latestAppointment.map((latestAppointment, index) => (
    <table key={index}>
    <tbody>
      <tr >
        <td rowSpan={2} style={{ border: "1px solid lightgray", backgroundColor: "lightgray", paddingLeft: "10px", width: "300px" }}>
          {latestAppointment.title} <br />
          {format(latestAppointment.start, "MM/dd/yyyy EEEE")} <br />
          {format(latestAppointment.start, "h:mm a")} - {format(latestAppointment.end, "h:mm a")}
        </td>
      </tr>
      <br/>
      <tr >
        <td>
          Date:
          <select name="sched" id="sched">
            <option value="11/13/2023">November 13, 2023 (MONDAY)</option>
            <option value="11/20/2023">November 20, 2023 (MONDAY)</option>
            <option value="11/27/2023">November 27, 2023 (MONDAY)</option>
            <option value="12/04/2023">December 4, 2023 (MONDAY)</option>
          </select>
        </td>
      </tr>
      <tr key={2}>
        <td>
          <button style={{ marginTop: "10px", borderRadius: 0, width: "100%" }}>Submit</button>
        </td>
      </tr>
    </tbody>
  </table>
       ))}

</div>
          </div>
          <PatientFooter />
        </div>
      );
}

export default PatientResched
