import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import DoctorCalendar from '../../components/DoctorCalendar';
import { useParams } from 'react-router-dom';
import { startOfWeek, addDays, format } from 'date-fns';
const SetAppointment = () => {
  const { doctorId } = useParams();
  const [error, setError] = useState(null);
  const [selectedDay, setSelectedDay] = useState('');
  const [selectedSched, setSelectedSched] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [schedules, setSchedules] = useState([
    {
      scheduleId: '',
      clinicId: '',
      avatar: '',
      doctorName: '',
      specialization: '',
      contactNumber: '',
      docEmail: '',
      scheduleDay: '',
      start: new Date(),
      end: new Date(),  // 5:00 PM
      slots: '',
      clinicName: '',
      address: '',
      number: '',
      email: ''
    },
  ]);

  useEffect(() => {
    const fetchDoctorSchedules = async () => {
      try {
        const response = await fetch(`http://localhost:8080/docsched/${doctorId}`);

        if (response.ok) {
          const data = await response.json();

          const formattedSchedules = data.map(schedule => ({
            scheduleId: schedule.scheduleId,
            clinicId: schedule.clinicId,
            avatar: schedule.doctor.user.avatar,
            doctorName: `${schedule.doctor.user.firstName} ${schedule.doctor.user.lastName}`,
            specialization: schedule.doctor.specialization,
            contactNumber: schedule.doctor.user.contactNumber,
            docEmail: schedule.doctor.user.email,
            scheduleDay: schedule.scheduleDay,
            start: schedule.startTime,
            end: schedule.endTime,
            slots: schedule.slots,
            clinicName: schedule.clinic.name,
            address: schedule.clinic.address,
            number: schedule.clinic.officeNumber,
            email: schedule.clinic.officeEmail
          }));

          setSchedules(formattedSchedules);

        } else {
          setError('Error fetching appointment');
        }
      } catch (error) {
        setError('Error fetching appointment');
      }
    };

    fetchDoctorSchedules();
  }, [doctorId]);

  const convertMilitaryToStandardTime = (militaryTime) => {
    if (typeof militaryTime !== 'string' || militaryTime.length !== 8) {
      return 'Invalid Time';
    }

    const [hours, minutes] = militaryTime.split(':');
    const parsedHours = parseInt(hours, 10);
    const period = parsedHours >= 12 ? 'PM' : 'AM';
    const formattedHours = parsedHours % 12 || 12;
    return `${formattedHours}:${minutes} ${period}`;
  };






  const getNearestDays = (selectedDay, numberOfOccurrences) => {
    const today = new Date();
    const daysInWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const selectedDayIndex = daysInWeek.findIndex(day => day.toLowerCase() === selectedDay.toLowerCase());

    if (selectedDayIndex === -1) {
      return []; // Invalid selected day
    }

    const startOfSelectedWeek = startOfWeek(today, { weekStartsOn: selectedDayIndex });
    const nearestDays = Array.from({ length: numberOfOccurrences }, (_, index) =>
      format(addDays(startOfSelectedWeek, index), 'yyyy-MM-dd')
    );

    return nearestDays;
  };

  const uniqueClinics = [...new Set(schedules.map(schedule => schedule.clinicName))];
  const [selectedClinicId, setSelectedClinicId] = useState('');


  const handleChangeClinic = (clinicName) => {
    const selectedClinic = schedules.find(schedule => schedule.clinicName === clinicName);
    setSelectedClinicId(selectedClinic?.clinicId || '');
    setSelectedDay('');
    setSelectedDate('');

  };

  const handleBookAppointment = () => {
    // Implement the logic to book an appointment using the selected values
    console.log('Clinic ID:', selectedClinicId);
    console.log('Selected Day:', selectedDay);
    console.log('Selected Timeslot:', selectedSched);
    console.log('Selected Date:', selectedDate);
  };

  return (
    <div>
      <PatientNavBar />
      <div style={{ display: "flex", margin: "auto", width: "100vw", justifyContent: "center" }}>
        <DoctorCalendar />

        <div style={{ marginLeft: "30px" }}>
          <h1>Dr. {schedules[0].doctorName}</h1>
          <h2>Specialization: {schedules[0].specialization}</h2>
          <h3>Contact Number: {schedules[0].contactNumber}</h3>
          <h3>Email Address: {schedules[0].docEmail}</h3>
          <br />

          <table>
            <tr>
              <td>
                <h2 style={{ color: "#0094d4" }}>Clinic: </h2>
              </td>
              <td>
                <select
                  name="clinic"
                  id="clinic"
                  style={{
                    width: "250px",
                    fontSize: "1.5rem",
                    fontWeight: "bold",
                    color: "#0094d4",
                  }}
                  onChange={(e) => handleChangeClinic(e.target.value)}
                >
                  <option value="">Select Clinic</option>
                  {uniqueClinics.map((clinicName, index) => (
                    <option key={index} value={clinicName}>
                      {clinicName}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
            <tr>
              <td><h5>Address:</h5></td>
              <td>{schedules.find(schedule => schedule.clinicId === selectedClinicId)?.address}</td>
            </tr>
            <tr>
              <td><h5>Contact Number:</h5></td>
              <td>{schedules.find(schedule => schedule.clinicId === selectedClinicId)?.number}</td>
            </tr>
            <tr>
              <td><h5>Email Address:</h5></td>
              <td>{schedules.find(schedule => schedule.clinicId === selectedClinicId)?.email}</td>
            </tr>
            <tr>
              <td style={{ width: "150px" }}>Day: </td>
              <td>
                <select
                  name="sched"
                  id={`day-${selectedClinicId}`}
                  style={{ width: "250px" }}
                  value={selectedDay}
                  onChange={(e) => {
                    setSelectedDay(e.target.value);
                    setSelectedSched('');
                    setSelectedDate('');
                    console.log("Selected Day changed:", e.target.value);
                  }}
                >
                  {/* Initial blank option */}
                  <option value="">Select Day</option>
                  {/* Options for the selected clinic's unique days */}
                  {[...new Set(schedules
                    .filter(schedule => schedule.clinicId === selectedClinicId)
                    .map(schedule => schedule.scheduleDay))]
                    .map(scheduleDay => (
                      <option key={`${selectedClinicId}-${scheduleDay}`} value={scheduleDay}>
                        {scheduleDay}
                      </option>
                    ))}
                </select>

              </td>
            </tr>
            <tr>
              <td style={{ width: "150px" }}>Timeslot: </td>
              <td>
                <select
                  name="timeslot"
                  id={`timeslot-${selectedClinicId}`}
                  style={{ width: "250px" }}
                  value={selectedSched}
                  onChange={(e) => {
                    const selectedTimeslot = e.target.value;
                    setSelectedSched(selectedTimeslot);
                    console.log('Selected Schedule ID:', selectedTimeslot);
                  }}
                >
                  {schedules
                    .filter(schedule => schedule.clinicId === selectedClinicId && schedule.scheduleDay === selectedDay)
                    .map(schedule => (
                      <option key={schedule.scheduleId} value={schedule.scheduleId}>
                        {convertMilitaryToStandardTime(schedule.start)} - {convertMilitaryToStandardTime(schedule.end)}
                      </option>
                    ))}
                </select>

              </td>
            </tr>

            {/* autogenerated dapat */}
            <tr>
              <td style={{ width: "150px" }}>Date: </td>
              <td>
              <select
  name="sched"
  id={`sched-${selectedClinicId}`}
  style={{ width: "250px" }}
  value={selectedDate}
  onChange={(e) => {
    const selectedDateString = e.target.value;
    // Save the date in 'yyyy-mm-dd' format to selectedDate
    setSelectedDate(selectedDateString);
    console.log('Selected Date:', selectedDateString);
  }}
>
  {/* Options for the selected clinic's nearest days (only 5) */}
  {schedules
    .filter(schedule => schedule.clinicId === selectedClinicId)
    .flatMap(schedule => {
      const nearestDays = getNearestDays(selectedDay, 5);
      return nearestDays.map(date => (
        <option
          key={`${selectedClinicId}-${schedule.scheduleId}-${date.toISOString()}`}
          value={date.toISOString().split('T')[0]}
        >
          {date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
          })}
        </option>
      ));
    })
    .slice(0, 5)} {/* Limit to the first 5 days */}
</select>

              </td>
            </tr>
            <tr><td colSpan={2}><button style={{ marginTop: "10px", borderRadius: 0, width: "100%" }}>Book an Appointment</button></td></tr>
          </table>

          <h1>Existing Appointments </h1>
          <table>
            <tr>
              <td rowSpan={2} width={200} style={{ border: "1px solid lightgray", backgroundColor: "lightgray", paddingLeft: "10px" }}>11/06/23 MONDAY <br /> 2:00-5:00 PM</td>
              <td><Link to="/patresched"><button style={{ borderRadius: 0, width: "200px" }}>Reschedule</button></Link></td>
            </tr>
            <tr><td><button className='cancel'>Cancel</button></td></tr>
          </table>
        </div>
      </div>
      <PatientFooter />
    </div>
  );
};

export default SetAppointment;
