import "../../styles/Search.css";
import DoctorNavbar from '../../components/DoctorNavbar';
import DoctorFooter from '../../components/DoctorFooter';
import React, { useState, useEffect } from 'react';
import ClinicCard from "../../components/Clinics";

const DoctorClinics= () => {
  const [loading, setLoading] = useState(true);
  const [clinicsResponse, setClinicsResponse] = useState('');
  const [doctorUserId, setDoctorUserId] = useState(''); // To store the patient's user ID
  const [isError, setIsError] = useState(false);
  const [schedules, setSchedules] = useState([
    {
      name: '',
      address: '',
      doctor: ''
    },
  ]);

  useEffect(() => {
      // Replace 'http://localhost:8080' with your actual API URL
      fetch('http://localhost:8080/checkLoggedInDoctor')
        .then((response) => {
          if (response.ok) {
            return response.json();
           
          }
          throw new Error('Network response was not ok');
        })
        .then((data) => {
          
          console.log({data});
          setDoctorUserId(data);
          // Once you have the patientUserId, make another request to get appointments
          fetch(`http://localhost:8080/docsched/${doctorUserId}`)
            .then((clinicsResponse) => {
              if (clinicsResponse.ok) {

                return clinicsResponse.json();
              }
              
              throw new Error('Network response was not ok');

            })
            .then((scheduleData) => {
              console.log(scheduleData);
              const formattedClinics = scheduleData.map((schedule) => {
                console.log(schedule.clinics.name);
                return {
                  name: '' + schedule.clinics.name,
                  address: '' + schedule.clinics.address,
                  doctor: ''+ schedule.doctor.user.firstName 
                };
              });
  
              setClinics(formattedClinics);
            })
            .catch((error) => {
              // Handle errors
              console.error(error);
            });
  
        })
        .catch((error) => {
          setIsError(true);
          console.error('Error:', error);
        });
    }, []);


  // if (loading) {
  //   return <p>Loading...</p>;
  // }

  const handleEdit = () => {
    console.log('Edit clicked for clinic:', );
  };

  const handleDelete = () => {

    console.log('Delete clicked for clinic:', );
  };

  return (
    <div className="info-container" id="container">
      <DoctorNavbar />
      <div className="info-container">
        <div className="doctor-grid">
        {clinics.map((schedules, index) => (
            <ClinicCard
              key={index}
              clinic={schedules}
              onEdit={handleEdit}
              onDelete={handleDelete}
            />
          ))}
        </div>
      </div>
      <DoctorFooter />
    </div>
  );
};

export default DoctorClinics;
