import React, { useState, useEffect } from 'react';
import "../../styles/Search.css";
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import DoctorCard from '../../components/Doctors'; // Import the DoctorCard component


const DoctorSearch = () => {
  // Sample doctor data (you can replace it with your own data)
  const [doctors, setDoctors] = useState([
    {
      name: '',
      specialization: '',
      credentials: ''
    }
  ]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAllDoctors = async () => {
      try {
        const response = await fetch('http://localhost:8080/allusers');

        if (response.ok) {
          const data = await response.json();
          const formattedDoctors = data.map((doctorData) => ({
            name: `Dr. ${doctorData.user.firstName} ${doctorData.user.middleName} ${doctorData.user.lastName}`,
            specialization: doctorData.specialization,
            credentials: doctorData.credentials
          }));

          setDoctors(formattedDoctors);
        } else {
          setError('Failed to fetch doctors');
        }
      } catch (error) {
        setError('Error while fetching doctors');
      } finally {
        setIsLoading(false);
      }
    };

    fetchAllDoctors();
  }, []);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }


  return (
    <div className="search-container" id="container">
      <PatientNavBar />
      <div className="info-container">
        <div class="search-box">
          <input type="text" class="search-input" placeholder="Find the doctor you need.."/>
          <button class="search-button">Search</button>
        </div>
        <div className="doctor-grid">
          {doctors.map((doctor, index) => (
            <DoctorCard key={index} doctor={doctor} />
          ))}
        </div>
        {/* The rest of your content */}
      </div>
      <PatientFooter />
    </div>
  );
};

export default DoctorSearch;
