import React, { useState } from 'react';
import './PatientRegister.css'; // Import your CSS file

function PatientRegister() {
    const [formData, setFormData] = useState({
        username: '',
        lastName: '',
        middleName: '',
        firstName: '',
        age: '',
        sex: '',
        birthday: '',
        address: '',
        contact: '',
        email: '',
        senior: '',
        pwdId: '',
        philhealthId: '',
        hmo: '',
        password: '',
      });

      const [signupMessage, setSignupMessage] = useState('');
      const [showVerification, setShowVerification] = useState(false);

      const handleSignup = async () => {
        try {
          // Define an array of fields that are required
          const requiredFields = [
            'username',
            'lastName', // Change 'lastname' to 'lastName'
            'firstName', // Change 'firstname' to 'firstName'
            'age',
            'sex',
            'birthday',
            'address',
            'contact',
            'email',
            'password',
          ];

          // Check if any of the required fields are empty
          for (const field of requiredFields) {
            if (!formData[field]) {
              setSignupMessage(`Please fill in the "${field}" field.`);
              return;
            }
          }

          // Validate password length
          if (formData.password.length <= 7) {
            setSignupMessage('Password must be at least 8 characters long.');
            return;
          }

          // Validate email format using regular expression
          const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
          if (!emailRegex.test(formData.email)) {
            setSignupMessage('Invalid email format.');
            return;
          }

          // Make an HTTP request to your server endpoint for user registration
          const url = 'http://localhost:8080/patients'; // Adjust the URL as needed

          const response = await fetch(url, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
          });

          if (response.ok) {
            // Signup successful
            setSignupMessage('User added successfully');
            setShowVerification(true);
          } else {
            // Signup failed
            const errorMessage = await response.text();
            setSignupMessage(`Signup failed: ${errorMessage}`);
            // Handle the error or display an error message to the user
          }
        } catch (error) {
          console.error('Error during signup:', error);
          setSignupMessage('Error during signup. Please try again later.');
          // Handle the error or display an error message to the user
        }
      };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="reg-container" id="reg-container">
      <div className="register">
        <h1 style={{ color: '#0094d4' }}>Patient Register</h1>
        <form action="#" id="register-form">
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="text"
                id="username"
                placeholder="*Username"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
              />
            </div>
            <div className="reg-infield">
              <input
                type="text"
                id="lastname"
                placeholder="*Last Name"
                name="lastname"
                value={formData.lastName}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="text"
                id="middlename"
                placeholder="Middle Name"
                name="middlename"
                value={formData.middleName}
                onChange={handleInputChange}
              />
            </div>
            <div className="reg-infield">
              <input
                type="text"
                id="firstname"
                placeholder="*First Name"
                name="firstname"
                value={formData.firstName}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="number"
                id="age"
                placeholder="*Age"
                name="age"
                min="1"
                max="100"
                value={formData.age}
                onChange={handleInputChange}
              />
            </div>

            <div className="reg-infield">
              <input
                type="text"
                id="sex"
                placeholder="*Sex"
                name="sex"
                value={formData.sex}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="date"
                id="birthday"
                name="birthday"
                value={formData.birthday}
                onChange={handleInputChange}
              />
            </div>
            <div className="reg-infield">
              <input
                type="text"
                id="address"
                placeholder="*(House/Lot/Unit No., Street, Barangay, City/Town, Province)"
                name="address"
                value={formData.address}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="text"
                placeholder="*Contact Number"
                name="contact"
                value={formData.contact}
                onChange={handleInputChange}
              />
            </div>
            <div className="reg-infield">
              <input
                type="email"
                placeholder="Email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="text"
                placeholder="Senior Citizen ID Number"
                name="senior"
                value={formData.senior}
                onChange={handleInputChange}
              />
            </div>
            <div className="reg-infield">
              <input
                type="text"
                placeholder="PWD ID Number"
                name="pwd"
                value={formData.pwdId}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="text"
                placeholder="PhilHealth ID Number"
                name="philhealth"
                value={formData.philhealthId}
                onChange={handleInputChange}
              />
            </div>
            <div className="reg-infield">
              <input
                type="text"
                placeholder="Health Insurance/HMO"
                name="hmo"
                value={formData.hmo}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
              />
            </div>
            <div className="reg-infield">
              <input
                type="password"
                placeholder="Re-enter Password"
                name="password2"
                value={formData.password}
                onChange={handleInputChange}
              />
            </div>
          </div>
        </form>
        <button onClick={handleSignup}>Register</button>
        {signupMessage && <p>{signupMessage}</p>}
      </div>
    </div>
  );  
}

export default PatientRegister;
