import React, { useState, useEffect} from 'react';
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import "react-big-calendar/lib/css/react-big-calendar.css";
import { useParams } from 'react-router-dom';

const PatientResched = () => {
  const {appointmentId} = useParams();
  const [patientUserId, setPatientUserId] = useState(''); // To store the patient's user ID
  const [isError, setIsError] = useState(false);
  const [error, setError] = useState('');
  const [appointments, setAppointments] = useState([
    {
      title: '',
      start: new Date(),
      end: new Date(),  // 5:00 PM
      appointmentId: '',
      appointmentStatus: ''
    },
  ]);

  useEffect(() => {
    const fetchAppointment = async () => {
      try {
        const response = await fetch(`http://localhost:8080/appointment/${appointmentId}`);

        if (response.ok) {
          const data = await response.json();
          setAppointments(data);
        } else {
          setError('Error fetching appointment');
        }
      } catch (error) {
        setError('Error fetching appointment');
      } finally {
        setLoading(false);
      }
    };

    fetchAppointment();
  }, [appointmentId]);

    const locales = {
        "en-US": require("date-fns/locale/en-US")
      }

      const localizer = dateFnsLocalizer({
        format,
        parse,
        startOfWeek,
        getDay,
        locales
      })

      // Define your appointment data

      return (
        <div>
          <PatientNavBar />
          <div style={{ display: "flex", margin: "auto", width: "100vw", justifyContent: "center" }}>
            <Calendar
              localizer={localizer}
              events={appointments}
              startAccessor="start"
              endAccessor="end"
              style={{ height: 600, width: "65%" }}
            />

            <div style={{ marginLeft: "30px" }}>
              <h1>My Appointments</h1>
              <table>
                {appointments.map((appointment, index) => (
                  <tr key={index}>
                    <td rowSpan={2} style={{ border: "1px solid lightgray", backgroundColor: "lightgray", paddingLeft: "10px", width:"300px"}}>
                      {appointment.title} <br />
                      {format(appointment.start, "MM/dd/yyyy EEEE")} <br />
                      {format(appointment.start, "h:mm a")} - {format(appointment.end, "h:mm a")}
                    </td>
                  </tr>
                ))}
                <br/>
                <tr>
                  <td> Date: 
                    <select name="sched" id="sched">
                    <option value="11/13/2023">November 13, 2023 (MONDAY)</option>
                    <option value="11/20/2023">November 20, 2023 (MONDAY)</option>
                    <option value="11/27/2023">November 27, 2023 (MONDAY)</option>
                    <option value="12/04/2023">December 4, 2023 (MONDAY)</option>
                  </select>
                  </td>
                </tr>
                <tr><button style={{marginTop: "10px", borderRadius: 0, width:"100%"}}>Submit</button></tr>
              </table>
            </div>

          </div>
          <PatientFooter />
        </div>
      );
}

export default PatientResched
