              />
            </div>
            <div className="reg-infield">
              <input
                type="text"
                placeholder="PWD ID Number"
                id="pwdId"
                name="pwdId"
                value={pwdId}
                onChange={(e) => setPwdId(e.target.value)}
              />
            </div>
          </div>
          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="text"
                placeholder="PhilHealth ID Number"
                id="philhealthId"
                name="philhealthId"
