          <div className="reg-row">
            <div className="reg-infield">
              <input
                type="password"
                placeholder="*Password"
                id="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="reg-infield">
              <input
                type="password"
