import React, { useState, useEffect } from 'react';
import "../../styles/Search.css";
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';
import DoctorCard from '../../components/Doctors'; // Import the DoctorCard component


const DoctorSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [doctors, setDoctors] = useState([
    {
      doctorId: '',
      firstName: '',
      lastName: '',
      contactNumber: '',
      specialization: '',
      credentials: '',
      avatar:'',
      clinicName:'',
    }
  ]);
  const [schedules, setSchedules] = useState([
    {
      doctorUserId: '',
      clinicName: ''
    }
  ]);
  const [searchDoctors, setSearchDoctors] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSchedules = async () => {
      try {
        const response = await fetch('http://localhost:8080/schedules');
        
        if (!response.ok) {
          throw new Error('Failed to fetch schedules');
        }

        const schedulesData = await response.json();
        const formattedSchedules = schedulesData.map((schedulesData) => ({
          doctorUserId: schedulesData.doctorUserId,
          clinicName: schedulesData.clinic.name ,
        }));

        setSchedules(formattedSchedules);
      } catch (error) {
        console.error('Error fetching schedules:', error.message);
      }
    };

    fetchSchedules();
  }, []);

  useEffect(() => {
    const fetchAllDoctors = async () => {
      try {
        const response = await fetch('http://localhost:8080/allusers');

        if (response.ok) {
          const data = await response.json();
          const formattedDoctors = data.map((doctorData) => ({
            doctorId: doctorData.userId,
            firstName: `Dr. ${doctorData.user.firstName}`,
            lastName: doctorData.user.lastName,
            contactNumber: doctorData.user.contactNumber,
            specialization: doctorData.specialization,
            credentials: doctorData.credentials,
            avatar: doctorData.user.avatar
          }));

          setDoctors(formattedDoctors);
          setSearchDoctors(doctors);
        } else {
          setError('Failed to fetch doctors');
        }
      } catch (error) {
        setError('Error while fetching doctors');
      } finally {
        setIsLoading(false);
      }
    };

    fetchAllDoctors();
  }, []);

 

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }


  setSearchDoctors(doctors);
  const handleSearchChange = (e) => {
    const inputValue = e.target.value.toLowerCase();
  
    // If the search query is empty, show all doctors
    if (inputValue === "") {
      setSearchDoctors(doctors);
    } else {
      // Filter the original array based on the current input value
      const filteredDoctors = doctors.filter(
        (doctor) =>
          `${doctor.firstName} ${doctor.lastName} ${doctor.specialization} ${doctor.clinicName}`.toLowerCase().includes(inputValue)
      );
     
      // Update the state with the filtered doctors
      setSearchDoctors(filteredDoctors);
    }
  
    // Update the search query state
    setSearchQuery(inputValue);
  };
  
  
  return (
    <div className='search-container' id="search-container">
      <PatientNavBar />
      <div className="info-container">
        <div className="search-box" style={{ position: 'sticky' }}>
        <input
          type="text"
          className="search-input"
          placeholder="Find the doctor you need.."
          value={searchQuery}
          onChange={handleSearchChange}
        />
        {/* Render the filtered list or handle it in your main component */}
        <button className="search-button" onClick={handleSearchChange}>
          Search
        </button>
      </div>
        <br />
        <div style={{ display: "block", width: "90%", marginLeft: "12%"}}>
          <div className="doctor-grid">
            {searchDoctors.map((doctor, index) => (
              <DoctorCard key={index} doctor={doctor} />
            ))}
          </div>
        </div>
      </div>
      <PatientFooter />
    </div>
  );
};

export default DoctorSearch;
