import "../../styles/Search.css";
import DoctorNavbar from '../../components/DoctorNavbar';
import DoctorFooter from '../../components/DoctorFooter';
import React, { useState, useEffect, useRef } from 'react';
import ClinicCard from "../../components/Clinics";

const DoctorClinics= () => {
  const [loading, setLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const [schedules, setSchedules] = useState([]);
  const [doctorUserId, setDoctorUserId] = useState('');

  const doctorUserIdRef = useRef(doctorUserId);

  useEffect(() => {
    doctorUserIdRef.current = doctorUserId;
  }, [doctorUserId]);

  useEffect(() => {
    // Replace 'http://localhost:8080' with your actual API URL
    fetch('http://localhost:8080/checkLoggedInDoctor')
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok');
      })
      .then((data) => {
        console.log({ data });
        setDoctorUserId(data);
      })
      .catch((error) => {
        setIsError(true);
        console.error('Error:', error);
      });
  }, []);

  useEffect(() => {
    fetch(`http://localhost:8080/docsched/${doctorUserIdRef.current}`)
      .then((clinicsResponse) => {
        if (clinicsResponse.ok) {
          return clinicsResponse.json();
        }
        throw new Error('Network response was not ok');
      })
      .then((scheduleData) => {
        console.log('scheduleData:', scheduleData);

        const formattedClinics = [];
        const clinicIdsAdded = [];

        scheduleData.forEach((schedule) => {
          const clinicId = schedule.clinic.clinicId;

          if (!clinicIdsAdded.includes(clinicId)) {
            formattedClinics.push({
              name: '' + schedule.clinic.name,
              address: '' + schedule.clinic.address,
              doctor: '' + schedule.doctor.user.firstName,
            });

            clinicIdsAdded.push(clinicId);
          }
        });

        setSchedules(formattedClinics);
        setLoading(false);
      })
      .catch((error) => {
        setIsError(true);
        console.error(error);
      });
  }, [doctorUserIdRef.current]); // Include the doctorUserIdRef.current in the dependency array


  if (isError) {
    return <p>Error occurred while fetching data.</p>;
  }


  const handleEdit = () => {
    console.log('Edit clicked for clinic:', );
  };

  const handleDelete = () => {

    console.log('Delete clicked for clinic:', );
  };

  return (
    <div className="info-container" id="container">
      <DoctorNavbar />
      <div className="info-container">
        <div className="doctor-grid">
        {schedules.map((schedule, index) => (
            <ClinicCard
              key={index}
              clinic={schedule}
              onEdit={handleEdit}
              onDelete={handleDelete}
            />
          ))}
        </div>
      </div>
      <DoctorFooter />
    </div>
  );
};

export default DoctorClinics;
