import React, { useState, useEffect } from 'react';
import DoctorNavbar from '../../components/DoctorNavbar';
import DoctorFooter from '../../components/DoctorFooter';
import DoctorCalendar from '../../components/DoctorCalendar';
import { Link } from 'react-router-dom';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import "react-big-calendar/lib/css/react-big-calendar.css";

const DoctorAppointment = () => {
  const [isError, setIsError] = useState(false);
  const [doctorUserId, setDoctorUserId] = useState('');
  const [appointments, setAppointments] = useState([
    {
      title: '',
      clinic: '',
      address: '',
      start: new Date(),
      end: new Date(),  // 5:00 PM
      appointmentId: '',
      appointmentStatus: '',
      slots: ''
    },
  ]);


  useEffect(() => {
    // Replace 'http://localhost:8080' with your actual API URL
    fetch('http://localhost:8080/checkLoggedInDoctor')
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok');
      })
      .then((data) => {
        setDoctorUserId(data);
        // Once you have the patientUserId, make another request to get appointments
        fetch(`http://localhost:8080/docappointments?doctorUserId=${data}`)
          .then((appointmentsResponse) => {
            if (appointmentsResponse.ok) {
              return appointmentsResponse.json();
            }
            throw new Error('Network response was not ok');
          })
          .then((appointmentsData) => {
            const formattedAppointments = appointmentsData.map((appointment) => {
              // Extract date and time components
              const [year, month, day] = appointment.scheduleDate.split('-').map(Number);
              const [hours, minutes] = appointment.startTime.split(':').map(Number);
              const [hours2, minutes2] = appointment.endTime.split(':').map(Number);

              // Create Date objects for start and end times
              const startDate = new Date(year, month - 1, day, hours, minutes);
              const endDate = new Date(year, month - 1, day, hours2, minutes2);

              // Create an appointment object
              return {
                title: appointment.patientName,
                clinic: appointment.clinicName,
                address: appointment.address,
                start: startDate,
                end: endDate,
                appointmentId: appointment.transactionNo,
                appointmentStatus: appointment.status,
                slots: appointment.slots
              };
            });

            setAppointments(formattedAppointments);
            console.log(appointments);
          })
          .catch((error) => {
            // Handle errors
            console.error(error);
          });

      })
      .catch((error) => {
        setIsError(true);
        console.error('Error:', error);
      });
  }, []);

  const locales = {
    "en-US": require("date-fns/locale/en-US")
  }

  const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales
  })

  const CustomEvent = ({ event }) => (
    <div style={{ margin: '5px 0', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis'}}>
      <strong style={{ margin: '0px 0', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'normal' }}>
       {event.clinic}
      </strong>
      <p style={{ margin: '0px 0', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'normal'}}>
        {event.title}
      </p>
      <p style={{ margin: '0px 0', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'normal'}}>
        {event.appointmentStatus}
      </p>
      <p style={{ margin: '0px 0', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'normal'}}>
        {event.slots}
      </p>
    </div>
  );
  const filterAndAggregateAppointments = (appointments) => {
    // Create an object to store unique appointments based on title
    const uniqueAppointments = {};

    // Iterate through each appointment
    appointments.forEach(appointment => {
      const { title, clinic } = appointment;

      // Check if the title already exists in uniqueAppointments
      if (!uniqueAppointments[title]) {
        // If not, create an entry with an array containing the current appointment
        uniqueAppointments[title] = {
          title,
          clinics: [clinic],
        };
      } else {
        // If yes, append the clinic to the existing array
        uniqueAppointments[title].clinics.push(clinic);
      }
    });

    // Convert the values of uniqueAppointments object into an array
    const resultAppointments = Object.values(uniqueAppointments);

    return resultAppointments;
  };

  // Usage example
  const resultAppointments = filterAndAggregateAppointments(appointments);

  // Now, resultAppointments contains an array of unique appointments with clinics aggregated
  console.log(resultAppointments);


  const getBackgroundColor = (status) => {
    switch (status) {
      case 'Cancelled':
        return '#FCA694';
      case 'Rescheduled':
        return '#FFB97F';
      case 'Scheduled by Patient':
        return '#FFFFDC';
      case 'Approved by Doctor':
        return '#BAFFC4';
      default:
        return 'lightgray';
    }
  };

  const getBorderColor = (status) => {
    switch (status) {
      case 'Cancelled':
        return '#A41D00';
      case 'Rescheduled':
        return '#FF7400';
      case 'Scheduled by Patient':
        return '#F8F547';
      case 'Approved by Doctor':
        return '#48DE66';
      default:
        return 'lightgray';
    }
  };

  return (
    <div>
      <DoctorNavbar />
      <div style={{ display: "flex", margin: "auto", width: "100vw", justifyContent: "center" }}>
      <Calendar
          localizer={localizer}
          events={appointments}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 900, width: "65%" }}
          components={{
            event: CustomEvent, // Use the custom Event component
          }}
        />
         {appointments.filter(appointment => appointment).length > 0 ? (
        <div style={{ marginLeft: "30px" }}>
          <h1>My Clinic</h1>
          {resultAppointments.map((appointment, index) => (
          <table key={index}>
            {/* <tr>
              <td rowSpan={2} width={200} style={{ border: "1px solid lightgray", backgroundColor: "lightgray", paddingLeft: "10px" }}>11/06/23 MONDAY <br /> 2:00-5:00 PM</td>
              <td><Link to="/addremove"><button style={{ borderRadius: 0, width: "250px" }}>Add/Remove Slots</button></Link></td>
            </tr>
            <tr><td><button className='cancel' style={{ width: "100%" }}>Cancel Appointments</button></td></tr>
            <br /> */}
            <tr>
              <td>{appointment.title}</td>
              <td>
                {/* Display unique clinics for the current unique appointment */}
      {Array.from(new Set(appointment.clinics)).map((clinic, clinicIndex) => (
        <span key={clinicIndex}>{clinic}{clinicIndex !== appointment.clinics.length - 1 ? ', ' : ''}</span>
      ))}
              </td>
              <td><Link to="/manageappointments"><button style={{ borderRadius: 0, width: "250px" }}>Manage Appointments</button></Link></td>
            </tr>
          </table>

          ))}
          </div>
        ) : (
          // Display a message if there are no appointments with appointmentStatus === "Approved by Doctor"
          <div style={{marginLeft: "1%"}}>
            <p>You have no appointments yet.</p>
            <Link to="/docsearch">
              <button>Set an appointment now</button>
            </Link>
          </div>
        )}
      </div>
      <DoctorFooter />
    </div>
  );
};

export default DoctorAppointment;
