import React, { useState, useEffect } from 'react';
import "../../styles/Profile.css";
import PatientNavBar from '../../components/PatientNavBar';
import PatientFooter from '../../components/PatientFooter';

const EditPatientProfile = () => {
    const [user, setUser] = useState(null);
    const [isError, setIsError] = useState(false);
    const [username, setUsername] = useState('');
    const [firstName, setFirstName] = useState('');
    const [middleName, setMiddleName] = useState('');
    const [lastName, setLastName] = useState('');
    const [age, setAge] = useState('');
    const [sex, setSex] = useState('');
    const [birthday, setBirthday] = useState('');
    const [address, setAddress] = useState('');
    const [contactNumber, setContactNumber] = useState('');
    const [email, setEmail] = useState('');
    const [seniorId, setSeniorId] = useState('');
    const [pwdId, setPwdId] = useState('');
    const [philhealthId, setPhilhealthId] = useState('');
    const [hmo, setHmo] = useState('');
    const [password, setPassword] = useState('');
    const [retypePassword, setRetypePassword] = useState('');
    const [editMessage, setEditMessage] = useState('');
    const [userType] = useState('patient');
    const [editSuccessful, setEditSuccessful] = useState(false);

    useEffect(() => {
        fetchUser();
      }, []);

    const fetchUser = async () => {
        try {
          const response = await fetch("http://localhost:8080/patientprofile");
          if (response.ok) {
            const data = await response.json();
            setUser(data);
            setUsername(data.user.username);
            setFirstName(data.user.firstName);
            setMiddleName(data.user.middleName);
            setLastName(data.user.lastName);
            setAge(data.user.age);
            setSex(data.user.sex);
            setBirthday(data.user.birthday);
            setAddress(data.user.address);
            setContactNumber(data.user.contactNumber);
            setEmail(data.user.email);
            setSeniorId(data.user.seniorId);
            setPwdId(data.user.pwdId);
            setPhilhealthId(data.user.philhealthId);
            setHmo(data.user.hmo);
          } else {
            setIsError(true);
          }
        } catch (error) {
          console.error('Error fetching user:', error);
          setIsError(true);
        }
        
    };

    const handleEdit = async () => {
        // Check if any of the fields are empty
        if (
            !username ||
            !firstName ||
            !middleName ||
            !lastName ||
            !age ||
            !sex ||
            !birthday ||
            !address ||
            !contactNumber ||
            !email ||
            !password ||
            !retypePassword
        ) {
          setEditMessage('Please fill in all required fields.');
          return;
        }
      
        // Validate email format using regular expression
        const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
        if (!emailRegex.test(email)) {
          setEditMessage('Invalid email format.');
          return;
        }
      
        try {
          const url = new URL('http://localhost:8080/edit-profile');
          const userData = {
            user: {
            username,
            firstName,
            middleName,
            lastName,
            age,
            sex,
            birthday,
            address,
            contactNumber,
            email,
            password,
            userType
          },
            patient: {
            seniorId,
            pwdId,
            philhealthId,
            hmo
            }
          };
      
          const response = await fetch(url, {
            method: 'PUT', // Use PUT method for editing
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
          });
      
          if (response.ok) {
            // Edit successful
            setEditMessage('User details updated successfully');
            setEditSuccessful(true);
          } else {
            // Edit failed
            const errorMessage = await response.text();
            setEditMessage('Edit failed');
            // Handle the error or display an error message to the user
          }
        } catch (error) {
          console.error('Error during edit:', error);
          setEditMessage('Error during edit. Please try again later.');
          // Handle the error or display an error message to the user
        }
      };
      
  return (
    <div className="profile-container" id="container">
        <PatientNavBar />
        <div className="info-container"> 
        {user ? (
            <div className="p-steps">
                    <div className="profile-card">
                        <br />
                        <br />
                        <br />
                        <br />
                        <h1>Edit Patient Profile</h1>
                        <div className="profile-infield">
                            <input  
                            type="text"
                            id="username"
                            placeholder="*Username"
                            name="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            />
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="firstName"
                            placeholder="*First Name"
                            name="firstName"
                            value={firstName}
                            onChange={(e) => setFirstName(e.target.value)}/>
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="lastName"
                            placeholder="*Last Name"
                            name="lastName"
                            value={lastName}
                            onChange={(e) => setLastName(e.target.value)}/>
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="middleName"
                            placeholder="*Middle Name"
                            name="middleName"
                            value={middleName}
                            onChange={(e) => setMiddleName(e.target.value)}/>
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="contactNumber"
                            placeholder="*Contact Number"
                            name="contactNumber"
                            value={contactNumber}
                            onChange={(e) => setContactNumber(e.target.value)}/>
                        </div>
                    </div>
                    <div className="profile-card">
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <div className="profile-infield">
                            <input type="text"
                            id="email"
                            placeholder="*Email"
                            name="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}/>
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="age"
                            placeholder="*Age"
                            name="age"
                            value={age}
                            onChange={(e) => setAge(e.target.value)}/>
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="sex"
                            placeholder="*Sex"
                            name="sex"
                            value={sex}
                            onChange={(e) => setSex(e.target.value)}/>
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="birthday"
                            placeholder="*Birthday"
                            name="birthday"
                            value={birthday}
                            onChange={(e) => setBirthday(e.target.value)}/>
                        </div>
                        <div className="profile-infield">
                            <input type="text"
                            id="address"
                            placeholder="*(House/Lot/Unit No., Street, Barangay, City/Town, Province)"
                            name="address"
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}/>
                        </div>
                    </div>
                    <div className="profile-card">
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <div className="profile-infield">
                            <input type="text" placeholder="Change PhilHealth ID No." />
                        </div>
                        <div className="profile-infield">
                            <input type="text" placeholder="Change PWD ID No." />
                        </div>
                        <div className="profile-infield">
                            <input type="password" placeholder="Change Password" />
                        </div>
                        <div className="profile-infield">
                            <input type="password" placeholder="Confirm Password" />
                        </div>
                        <button type="submit">Save Changes</button>
                    </div>
                    <div className="profile-card" style={{marginTop:'100px'}}>
                        <img
                            src={require('../../images/profilepic.jpg')}
                            alt='profilepic'
                            style={{ maxWidth: '80%', maxHeight: '80vh', margin: 0}}
                        />
                    </div>
            </div>
            ) : (
                isError ? (
                  <p>Error fetching user profile</p>
                ) : (
                  <p>Loading user profile...</p>
                )
              )}
            <div className="steps"  style={{marginTop:'-10px'}}>
                <div className="step-card">

                    <img
                        src={require('../../images/vision.png')}
                        alt='vision'
                        style={{ maxWidth: '100%', maxHeight: '100hw', margin: 0}}
                    />
                </div>
                <div className="step-card" >
                    <br />
                    <br />
                    <br />
                    <br />
                    <p>At 'Doc Click Connect,' we're not just a platform for scheduling appointments;
                    <br/>we're your dedicated healthcare partner. Your well-being is our priority,
                    <br/>and we believe that staying informed is a crucial aspect of maintaining good health.
                    <br/>Whether it's guidance on managing chronic conditions, maintaining a healthy lifestyle, 
                    <br/>or understanding the latest medical advancements, we're here to empower you with knowledge.
                    <br />
                    <br /> Our commitment to your health goes beyond appointments. We're here to keep you informed 
                    <br />and support your journey to a healthier you. Check back regularly for updates, and feel 
                    <br />free to reach out to your healthcare provider for personalized advice and recommendations.</p>
                </div>
            </div>
        </div>
        <PatientFooter />
    </div>
  );
};

export default EditPatientProfile;
